// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

contract Auction is
    Initializable,
    PausableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    enum ListingStatus {
        Listing,
        Wait,
        Cancel,
        Paid
    }

    // enum OfferStatus {
    //     Offering,
    //     Deposit,
    //     Cancel,
    //     Refunded
    // }

    struct ListingInfo {
        address nftContract;
        uint256 tokenId;
        uint256 price;
        uint256 stepPrice;
        uint256 highestOfferId;
        uint256 highestPrice;
        uint256 startTime;
        uint256 endTime;
        ListingStatus listingStatus;
    }

    struct OfferInfo {
        uint256 offerId;
        address bidder;
        uint256 offerPrice;
        bool deposited;
        uint256 paidAmount;
        // OfferStatus offerStatus;
    }

    event ListingCreated(
        uint256 indexed id,
        address indexed nftContract,
        uint256 indexed tokenId,
        uint256 price,
        uint256 stepPrice,
        uint256 startTime,
        uint256 endTime
    );
    event ListingCanceled(uint256 indexed id);
    event DepositMade(
        uint256 indexed id,
        address indexed bidder,
        uint256 amount
    );
    event OfferMade(
        uint256 indexed id,
        address indexed bidder,
        uint256 price,
        uint256 offerId
    );
    event AuctionEnded(uint256 indexed id, uint256 offerId, uint256 endTime);
    event FundDeposited(
        uint256 indexed id,
        address indexed bidder,
        uint256 amount
    );
    event FundClaimed(
        uint256 indexed id,
        address indexed bidder,
        uint256 amount
    );
    event DepositWithdrawn(
        uint256 indexed id,
        address indexed bidder,
        uint256 amount
    );
    event FundClaimedTimeOut(
        uint256 indexed id,
        address indexed bidder,
        uint256 amount
    );

    mapping(uint256 => ListingInfo) public listings;
    mapping(uint256 => mapping(uint256 => OfferInfo)) public offers;
    mapping(uint256 => mapping(address => uint256)) public userOfferIds;
    mapping(uint256 => uint256) public nextOfferId;
    mapping(uint256 => mapping(address => bool)) public userHasDeposited;
    mapping(uint256 => uint256) public highestOfferId;

    mapping(uint256 => bool) public offerClaimed;
    mapping(uint256 => bool) public nftClaimed;
    mapping(uint256 => bool) public WithdrawnNFT;
    mapping(uint256 => bool) public WithdrawnFund;

    uint256 public nextListingId;
    uint256 public depositAmount;
    address public CQTQ;
    address public paymentToken;
    uint256 public deadline;
    uint256 private depositTime;

    constructor() {
        _disableInitializers();
    }

    function initialize(
        address initialOwner,
        address _CQTQ,
        address _paymentToken
    ) public initializer {
        __Pausable_init();
        __Ownable_init(initialOwner);
        __UUPSUpgradeable_init();
        CQTQ = _CQTQ;
        paymentToken = _paymentToken;
    }

    function setDepositAmount(uint256 _depositAmount) public onlyOwner {
        depositAmount = _depositAmount;
    }

    function setDeadline(uint256 _deadline) public onlyOwner {
        deadline = _deadline;
    }

    function setDepositTime(uint256 _depositTime) public onlyOwner {
        depositTime = _depositTime;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function createListing(
        address _nftContract,
        uint256 tokenId,
        uint256 _price,
        uint256 _stepPrice,
        uint256 _startTime,
        uint256 _duration
    ) public onlyOwner returns (uint256) {
        require(_duration > 0, "Duration must be greater than zero");
        uint256 durationInSeconds = _duration * 60;
        uint256 endTime = _startTime + durationInSeconds;

        require(endTime > block.timestamp, "End time must be in the future");

        IERC721 nft = IERC721(_nftContract);
        require(nft.ownerOf(tokenId) == msg.sender, "You don't own this token");

        uint256 id = nextListingId;
        nextListingId++;

        ListingInfo memory newListing = ListingInfo(
            _nftContract,
            tokenId,
            _price,
            _stepPrice,
            0,
            0,
            _startTime,
            endTime,
            ListingStatus.Listing
        );

        listings[id] = newListing;

        IERC721(_nftContract).transferFrom(msg.sender, address(this), tokenId);
        emit ListingCreated(
            id,
            _nftContract,
            tokenId,
            _price,
            _stepPrice,
            _startTime,
            endTime
        );
        return id;
    }

    function cancelListing(uint256 id) public onlyOwner {
        ListingInfo storage listing = listings[id];

        require(
            listing.listingStatus == ListingStatus.Listing,
            "Listing must be in Listing status to cancel"
        );

        if (block.timestamp < listing.endTime) {
            listing.endTime = block.timestamp;
        }

        address nftContract = listing.nftContract;
        uint256 tokenId = listing.tokenId;
        listing.listingStatus = ListingStatus.Cancel;

        IERC721(nftContract).transferFrom(address(this), msg.sender, tokenId);
        emit ListingCanceled(id);
    }

    function deposit(uint256 id) public {
        ListingInfo storage listing = listings[id];

        require(
            listing.listingStatus == ListingStatus.Listing,
            "Listing must be in Listing status"
        );
        uint256 depositDeadline = listing.startTime - depositTime;
        require(block.timestamp < depositDeadline, "Listing not availble");
        require(!userHasDeposited[id][msg.sender], "Already dep");

        IERC20(paymentToken).transferFrom(
            msg.sender,
            address(this),
            depositAmount
        );
        userHasDeposited[id][msg.sender] = true;
        emit DepositMade(id, msg.sender, depositAmount);
    }

    function makeOffer(uint256 id, uint256 _price) public returns (uint256) {
        ListingInfo storage listing = listings[id];
        uint256 highestPrice = listing.highestPrice;

        require(
            _price > highestPrice + listing.stepPrice,
            "Offer price must be greater than price"
        );
        require(userHasDeposited[id][msg.sender], "Not dep yet");
        require(
            listing.listingStatus == ListingStatus.Listing,
            "Listing must be in Listing status"
        );
        require(
            block.timestamp < listing.endTime &&
                block.timestamp > listing.startTime,
            "Listing not availble"
        );
        require(
            _price > listing.price,
            "Offer price must be greater than price"
        );

        uint256 offerId = nextOfferId[id];
        nextOfferId[id]++;

        OfferInfo memory newOffer = OfferInfo(
            offerId,
            msg.sender,
            _price,
            true,
            0
            // OfferStatus.Offering
        );

        offers[id][offerId] = newOffer;

        // Store ID of an offer per address
        userOfferIds[id][msg.sender] = offerId;

        listing.highestPrice = _price;
        listing.highestOfferId = offerId;

        emit OfferMade(id, msg.sender, _price, offerId);
        return offerId;
    }

    function endAuction(uint256 id) public onlyOwner {
        ListingInfo storage listing = listings[id];
        // uint256 offerId = listing.highestOfferId;
        // OfferInfo storage offer = offers[id][offerId];

        require(
            listing.listingStatus == ListingStatus.Listing,
            "Listing must be in Listing status"
        );

        require(block.timestamp > listing.endTime, "Listing not ended");

        listing.listingStatus = ListingStatus.Wait;
        // offer.offerStatus = OfferStatus.Deposit;
        listing.endTime = block.timestamp;
        emit AuctionEnded(id, listing.highestOfferId, listing.endTime);
    }

    function fund(uint256 id, uint256 amount) public {
        ListingInfo storage listing = listings[id];
        uint256 offerId = listing.highestOfferId;
        OfferInfo storage offer = offers[id][offerId];

        require(
            listing.listingStatus == ListingStatus.Wait,
            "listing must be in wait status"
        );
        // require(
        //     offer.offerStatus == OfferStatus.Deposit,
        //     "offer must be in Deposit status"
        // );
        require(offer.offerId == offerId, "Invalid offer ID");
        require(msg.sender == offer.bidder, "Not offer winner");
        require(block.timestamp < listing.endTime + deadline, "Timeout");

        //Cal remain amount have to paid after
        uint256 haveToPayAmount = offer.offerPrice - depositAmount;
        uint256 remaining = haveToPayAmount - offer.paidAmount;

        require(remaining > 0, "Already paid");

        if (amount > remaining) {
            amount = remaining;
        }

        IERC20(paymentToken).transferFrom(msg.sender, address(this), amount);
        offer.paidAmount += amount;
        if (amount >= remaining) {
            IERC721(listing.nftContract).transferFrom(
                address(this),
                msg.sender,
                listing.tokenId
            );
            listing.listingStatus = ListingStatus.Paid;
        }
        emit FundDeposited(id, msg.sender, amount);
    }

    function withdrawDeposit(uint256 id) public {
        ListingInfo storage listing = listings[id];

        require(
            listing.listingStatus != ListingStatus.Listing ||
                block.timestamp > listing.endTime,
            "Listing must not be in Listing status or the listing has not ended yet."
        );
        require(userHasDeposited[id][msg.sender], "Not deposited");

        IERC20(paymentToken).transfer(msg.sender, depositAmount);
        userHasDeposited[id][msg.sender] = false;
        emit DepositWithdrawn(id, msg.sender, depositAmount);
    }

    function claimFundTimeOut(uint256 id) public {
        ListingInfo storage listing = listings[id];
        uint256 offerId = listing.highestOfferId;
        OfferInfo storage offer = offers[id][offerId];

        require(
            listing.listingStatus == ListingStatus.Wait,
            "listing must be in Wait status"
        );

        require(
            offer.paidAmount < offer.offerPrice - depositAmount,
            "Already enough money"
        );
        require(block.timestamp > listing.endTime + deadline, "Not timeout yet");
        require(msg.sender == offer.bidder, "Not winner");
        require(!WithdrawnFund[id], "Offer already claimed");

        address nftContract = listing.nftContract;
        uint256 tokenId = listing.tokenId;

        IERC721(nftContract).transferFrom(address(this), CQTQ, tokenId);
        IERC20(paymentToken).transfer(msg.sender, offer.paidAmount);
        WithdrawnFund[id] = true;
        emit FundClaimedTimeOut(id, msg.sender, offer.paidAmount);
    }

    function getStatusListing(uint256 id) public view returns (ListingStatus) {
        ListingInfo storage listing = listings[id];
        return listing.listingStatus;
    }

    // function getStatusOffer(uint256 id, uint256 offerId)
    //     public
    //     view
    //     returns (OfferStatus)
    // {
    //     OfferInfo storage offer = offers[id][offerId];
    //     return offer.offerStatus;
    // }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyOwner
    {}
}

